// Calculate distance for multiple modes
const origin = $json.origin;
const destination = $json.destination;
const modes = $json.modes || ['driving', 'walking', 'transit'];
const apiKey = $json.api_key;

const results = {};

for (const mode of modes) {
  const url = `https://maps.googleapis.com/maps/api/distancematrix/json?origins=${encodeURIComponent(origin)}&destinations=${encodeURIComponent(destination)}&mode=${mode}&key=${apiKey}`;

  const response = await $http.get(url);

  if (response.data.status === 'OK') {
    const element = response.data.rows[0].elements[0];
    if (element.status === 'OK') {
      results[mode] = {
        distance: element.distance.text,
        duration: element.duration.text,
        distance_value: element.distance.value,
        duration_value: element.duration.value
      };
    }
  }
}

return [{ json: { origin, destination, results } }];
